mapper.select:
select
 no as "no",
 writer as "writer",
 title as "title",
 content as "content",
 createDate as "createDate",
 readCount as "readCount",
 RNUM+1 as "seq"
from (
 select no, writer, title, content, createDate, readCount, ROWNUM as RNUM
 from (
  select * from board
  order by no desc
 )
)
where RNUM between #{start} and #{end}
